  //       await getDocs(
  //         query(
  //           collection(db, "paymentRecords"),
  //           where("recipientData.reciepientUID", "==", uid),
  //           where("transactionType", "==", "Creator Support"),
  //           limit(3)
  //         )
  //       )
  //         .then((snapshot) => {
  //           snapshot.forEach((docs) => {
  //             if (docs.exists()) {
  //               transactions.push(docs.data());
  //             } else {
  //               transactions.push("Null");
  //             }
  //           });
  //           console.log(transactions);
  //         })
  //         .catch((error) => {
  //           console.log(error);
  //         });
  //       // console.log(transactions[0].amount)
  //     } else {
  //       console.log("Not Logged In");
  //     }
  //   };
  //   // console.log(transactionData[0].amount)

  //   run();
  // }, [uid, loggedIn]);

  if (active && payload && payload.length) {
    return (
      <div className={styles.customTooltip}>
        <p className={styles.label}>{`â‚¹ ${payload[0].value}`}</p>
      </div>
    );
  }

  return null;
};

const SalesChart = () => {
  return (
    <>
      <div className={clsx("container", styles.mainDiv)}>
        <div className="d-flex">
          <h2 className={styles.h2}>Sales Chart</h2>
          <div className={clsx(styles.selectDiv)}>
            <Form.Select
              name="bank acc"
              className={clsx(styles.select, "mb-3")}
              aria-label="Default select example"
            >
              <option value="1">Monthly</option>
              <option value="2">Weekly</option>
              <option value="3">All Time</option>
            </Form.Select>
          </div>
        </div>
        <div className={styles.chart}>
          <ResponsiveContainer>
            <LineChart
              data={data}
              margin={{
                top: 5,
                right: 30,
                bottom: 5,
              }}
            >
              <defs>
                <linearGradient
                  id="colorGradient"
                  x1="4%"
                  y1="49%"
                  x2="85%"
                  y2="92%"
                >
                  <stop offset="4%" stopColor="rgba(231, 13, 140, 1)" />
                  <stop offset="49%" stopColor="rgba(231, 13, 140, 1)" />
                  <stop offset="85%" stopColor="rgba(246, 164, 76, 1)" />
                  <stop offset="92%" stopColor="rgba(252, 199, 55, 1)" />
                </linearGradient>
              </defs>

              <CartesianGrid strokeDasharray="3 3" vertical={false} />
              <XAxis
                dataKey="name"
                padding={{ left: 30, right: 30 }}
                axisLine={false}
                tickMargin={10}
                tickLine={false}
                tick={{
                  fill: "#8c8c8c",
                  fontFamily: "Poppins, sans-serif",
                  fontSize: 13,
                  fontWeight: 600,
                }}
              />
              <YAxis
                axisLine={false}
                tickMargin={10}
                tickLine={false}
                tick={{
                  fill: "black",
                  fontFamily: "Poppins, sans-serif",
                  fontSize: 13,
                  fontWeight: 700,
                }}
              />
              <Tooltip
                content={<CustomTooltip />}
                cursor={{ stroke: "#ED0090" }}
                offset={0}
              />
              <Legend />
              <Line
                type="monotone"
                dataKey="uv"
                legendType="none"
                stroke="url(#colorGradient)"
                dot={false}
                strokeWidth={2}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
    </>
  );
};

export default SalesChart;
